import React from 'react';
import logo from './logo.svg';
import './App.css';

export default class App extends React.Component {
	constructor(props) {
			super(props);
			this.state = {
				webTableData : []
			}
	}
	getTableDataFromWeb = function() {
		fetch('http://localhost:3000/getTableData', {
		  headers: {
			'Content-Type': 'application/json'
		  }
		})
		.then(res => res.json())
		.then(res => {
			//this.refreshPage();
			this.webTableData= res.response;
			console.log(this.webTableData);
		})
	}

	predictMatch = function (){
		var club1= document.getElementById("club1Id").value;
		var club2= document.getElementById("club2Id").value;
		var webData = this.webTableData;
		var result="";
		var club1Points=0;
		var club2Points=0;
		for(var i in webData){
			if(webData[i].club.toLowerCase() == club1.toLowerCase()){
				club1Points=webData[i].points;
			}
			if(webData[i].club.toLowerCase() == club2.toLowerCase()){
				club2Points=webData[i].points;
			}
		}
		console.log("club1Points "+club1Points);
		console.log("club2Points "+club2Points);

		var finalResult = club1Points-club2Points;
		if(finalResult>0){
			result=club1;
		} else {
			result=club2;
		}
		document.getElementById("webData").innerText=result;
	}

	refreshPage = function() {
	  window.location.reload();
	}
	render() {
		this.getTableDataFromWeb();
		return (
			<div >
				<input id="club1Id" placeholder="enter club name"></input>
				<input id="club2Id" placeholder="enter club name"></input>
				<button id="callButton" onClick={()=>this.predictMatch()}>predict</button>
				<div id="webData"></div>


			</div>
		);
	}
}
