import React from 'react';
import logo from './logo.svg';
import './App.css';

export default class App extends React.Component {
	constructor(props) {
			super(props);
			this.state = {
				webTableData : []
			}
	}
	getTableDataFromWeb = function() {
		fetch('http://localhost:3000/getTableData', {
		  headers: {
			'Content-Type': 'application/json'
		  }
		})
		.then(res => res.json())
		.then(res => {
			//this.refreshPage();
			this.webTableData= res.response;
			console.log(this.webTableData);
		})
	}

	predictMatch=function(team1,team2){
		var m1=this.webTableData.match(team1);
		var m2=this.webTableData.match(team2);
		console.log(m1);
		console.log(m2);
		document.getElementById("webData").innerText=m1;
	}

	refreshPage = function() {
	  window.location.reload();
	}
	render() {
		return (
			<div >
				<button id="callButton" onClick={()=>this.getTableDataFromWeb()}>CLickMe</button>
				<input id="club1Id" placeholder="enter club name"></input>
				<input id="club2Id" placeholder="enter club name"></input>
				<div id="webData"></div>
				<button id="callButton" onClick={()=>this.predictMatch()}>predict</button>

			</div>
		);
	}
}
