			if(clubData.club.toLowerCase() == club1.toLowerCase()){
				club1Data = clubData;
				return clubData;
			} else if(clubData.club.toLowerCase() == club2.toLowerCase()){
				club2Data = clubData;
				return clubData;
			}
		});
